// Parse the incoming webhook data
const data = $input.first().json;

// Extract relevant information
const activity = {
  timestamp: data.timestamp,
  type: data.type,
  description: data.description,
  severity: data.severity,
  computer: data.computer,
  user: data.user,
  details: data.details || {}
};

// Determine email subject based on severity
let subject = `[${activity.severity.toUpperCase()}] `;

switch(activity.type) {
  case 'UsbDrive':
    subject += 'USB Drive Activity Detected';
    break;
  case 'UsbBlocked':
    subject += 'USB DEVICE BLOCKED';
    break;
  case 'FileTransfer':
    subject += 'File Transfer Activity Detected';
    break;
  case 'AppInstallation':
    subject += 'Application Installation Detected';
    break;
  case 'BlacklistedApp':
    subject += 'BLACKLISTED APPLICATION DETECTED';
    break;
  case 'NetworkActivity':
    subject += 'Suspicious Network Activity Detected';
    break;
  default:
    subject += 'System Activity Detected';
}

// Create email body
let body = `Employee Activity Alert\n\n`;
body += `Time: ${activity.timestamp}\n`;
body += `Computer: ${activity.computer}\n`;
body += `User: ${activity.user}\n`;
body += `Activity Type: ${activity.type}\n`;
body += `Severity: ${activity.severity}\n`;
body += `Description: ${activity.description}\n\n`;

// Add details if available
if (Object.keys(activity.details).length > 0) {
  body += 'Details:\n';
  for (const [key, value] of Object.entries(activity.details)) {
    body += `  ${key}: ${value}\n`;
  }
}

// Special handling for USB blocking events
if (activity.type === 'UsbBlocked') {
  body += '\n⚠️ SECURITY ALERT ⚠️\n';
  body += 'An unauthorized USB device was blocked from connecting to the computer.\n';
  body += 'This device was not found in the approved whitelist.\n\n';
  body += 'Immediate action may be required to investigate this security incident.\n';
}

return {
  subject: subject,
  body: body,
  severity: activity.severity,
  type: activity.type,
  computer: activity.computer,
  user: activity.user
};
